import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Offcanvas } from 'react-bootstrap';
import Three_Images from './Col-ThreeBlock';

function Sidebar() {
    const [showSidebar, setShowSidebar] = useState(false);

    const handleSidebarToggle = () => setShowSidebar(!showSidebar);

    return (
        <Container fluid>
            <Row>
                {/* Button to show sidebar on small screens */}
                <Col xs={12}>
                    <Button 
                        variant="outline-dark" 
                        onClick={handleSidebarToggle} 
                        className="d-md-none mb-3"
                    >
                        SHOP BY
                    </Button>
                </Col>

                {/* Sidebar - Offcanvas for small screens */}
                <Offcanvas show={showSidebar} onHide={handleSidebarToggle} placement="start">
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>SHOPPING OPTIONS</Offcanvas.Title>
                    </Offcanvas.Header>
                    <Offcanvas.Body>
                        {['COLOR FAMILY', 'SEASON', 'PRODUCT CATEGORY', 'SIZE', 'FABRIC'].map((option, index) => (
                            <div
                                key={index}
                                style={{
                                    height: '40px',
                                    width: '100%',
                                    display: 'flex',
                                    justifyContent: 'left',
                                    alignItems: 'center',
                                    borderBottom: '1px solid lightgrey',
                                }}
                            >
                                <Form.Select
                                    style={{
                                        border: 'none',
                                        width: '95%',
                                        fontSize: 'small',
                                        fontFamily: "'Times New Roman', Times, serif",
                                        color: 'darkslategrey',
                                    }}
                                >
                                    <option>{option}</option>
                                    <option>ABC DEF GHI JKL MNO PQR S</option>
                                </Form.Select>
                            </div>
                        ))}
                    </Offcanvas.Body>
                </Offcanvas>

                {/* Main Content */}
                <Col xs={12} md={8} lg={9}>
                    <div style={{ padding: '20px' }}>
                        {/* Your product display goes here */}
                        <Three_Images
                            img1="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                            name1="Multicolor Lawn Printed Unstitched 3PC"
                            price1="4,890.00"

                            img2="https://www.junaidjamshed.com/media/catalog/product/2/5/25-3247_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                            name2="Multicolor Lawn Printed Unstitched 3PC"
                            price2="4,890.00"

                            img3="https://www.junaidjamshed.com/media/catalog/product/2/5/25-3246_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                            name3="Multicolor Lawn Printed Unstitched 3PC"
                            price3="4,890.00"
                        />
                        <br /><br /><br />
                        {/* Add more products here */}
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default Sidebar;
