import React, { useState } from 'react';
import { Container, Row, Col, Image, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import CardSlider from './CardSlider';

const Cloth = () => {
  const [mainPic, setMainPic] = useState(
    'https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds'
  );

  const products = [
    {
        id: 1,
        title: "BLUE RAW SILK UNSTITCHED 3PC",
        price: "PKR 5,265.00",
        originalPrice: "PKR 10,530.00",
        discount: "50% OFF",
        imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/4/24-035_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2", // Replace with actual image paths
    },
    {
        id: 2,
        title: "MULTICOLOR LINEN PRINTED UNSTITCHED 3PC",
        price: "PKR 4,712.00",
        originalPrice: "PKR 5,890.00",
        discount: "20% OFF",
        imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/3/23-430_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
    },
    {
        id: 3,
        title: "BROWN LAWN UNSTITCHED 3PC",
        price: "PKR 3,380.00",
        imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/3/23-602_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
    },
    {
        id: 4,
        title: "MULTICOLOR LAWN UNSTITCHED 3PC",
        price: "PKR 3,380.00",
        imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/s/jst-24-1649_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
    },
];

const sliderSettings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 4,
  slidesToScroll: 3,
  responsive: [
      {
          breakpoint: 1024,
          settings: {
              slidesToShow: 3,
          },
      },
      {
          breakpoint: 768,
          settings: {
              slidesToShow: 2,
          },
      },
      {
          breakpoint: 576,
          settings: {
              slidesToShow: 1,
          },
      },
  ],
};


  return (
    <Container fluid style={{ height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Row className="w-100" style={{ height: '90%' }}>
        {/* Left Section */}
        <Col md={1} className="d-flex justify-content-center align-items-center"></Col>
        <Col md={4} className="d-flex justify-content-center align-items-center">
          <div style={{ width: '450px', height: '720px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div style={{ height: '78%', width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <Image
                id="mainpic"
                src={mainPic}
                alt="Main Product"
                style={{ width: '100%', height: '99%' }}
                fluid
              />
            </div>
            <div style={{ height: '18%', width: '70%', display: 'flex', justifyContent: 'space-evenly', alignItems: 'center' }}>
              <Image
                className="imgs"
                onClick={() => setMainPic('https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds')}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                style={{ width: '27%', height: '90%', cursor: 'pointer' }}
                fluid
              />
              <Image
                className="imgs"
                onClick={() => setMainPic('https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_2_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds')}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_2_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                style={{ width: '27%', height: '90%', cursor: 'pointer' }}
                fluid
              />
              <Image
                className="imgs"
                onClick={() => setMainPic('https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_3_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds')}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_3_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                style={{ width: '27%', height: '90%', cursor: 'pointer' }}
                fluid
              />
            </div>
          </div>
        </Col>

        {/* Right Section */}
        <Col md={1} className="d-flex justify-content-center align-items-center"></Col>
        
        <Col md={5} className="d-flex flex-column align-items-start">
          <h4
            style={{
              fontSize: "1.3rem",
            //   fontWeight: "bold",
              textTransform: "uppercase",
            }}
          >
            MULTICOLOR CAMBRIC PRINTED UNSTITCHED 3PC
          </h4>
          <p style={{ fontSize: "0.9rem", marginBottom: "5px", color:"grey" }}>
            SKU#: 10000080093
          </p>
          <p
            style={{
              fontSize: "0.9rem",
              fontWeight: "bold",
              color: "darkgrey",
              marginBottom: "10px",
            }}
          >
            IN STOCK
          </p>
          <p style={{ fontSize: "0.9rem", marginBottom: "15px" }}>
            <a
              href="#"
              style={{
                textDecoration: "underline",
                color: "black",
                fontSize: "0.9rem",
              }}
            >
              BE THE FIRST TO REVIEW THIS PRODUCT
            </a>
          </p>
          <div className="pricing ">
            <span
              style={{
                fontSize: "1.8rem",
                fontWeight: "bold",
                marginRight: "15px",
              }}
            >
              PKR 3,912.00
            </span>
            <span
              style={{
                textDecoration: "line-through",
                color: "grey",
                fontWeight: "bold",
                fontSize: "1.8rem",
              }}
            >
              PKR 4,890.00
            </span>
          </div>
          <hr className="w-100" style={{ borderColor: "gray" }} />

          <p
            style={{
              fontSize: "1rem",
              marginBottom: "20px",
              marginTop:"5px",
              
            }}
          >
            <span
              style={{
                color: "green",
                marginRight: "5px",
                fontSize: "1rem",
              }}
            >
              ‚óè
            </span>
            Limited Stock Alert: Get Yours Before They're Gone!
          </p>
          <Button
            variant="outline-dark"
            className="text-uppercase"
            style={{
              fontWeight: "bold",
              borderRadius: "0",
              padding: "10px",
              marginTop:"5px",
              marginBottom:"10px",
              border:"2px solid black",
              width:"65%",
            }}
          >
            Add to Bag
          </Button>
          <div className="product-details mt-4">
            <h5
              style={{
                textDecoration: "underline",
                fontSize: "1.2rem",
                marginBottom: "15px",
              }}
            >
              Basic 3 Piece
            </h5>
            <ul style={{ paddingLeft: "0px", fontSize: "0.9rem" }}>
              <li>Digital Printed Cambric Shirt: 1.92 Meters</li>
              <li>Digital Printed Cambric Dupatta: 2.5 Meters</li>
              <li>Cambric Trouser: 2.5 Meters</li>
            </ul>
            <p
              style={{
                fontSize: "0.9rem",
                color: "grey",
                marginTop: "15px",
                fontStyle:"italic",
              }}
            >
              * The stitch style and decorative accessories featured in the dress
              are for illustrative purposes only and are not included as part of
              the product.
            </p>
          </div>

          {['MORE INFORMATION', 'REVIEWS'].map((option, index) => (
              <div
                key={index}
                style={{
                  height: '40px',
                  width: '100%',
                  display: 'flex',
                  justifyContent: 'left',
                  alignItems: 'center',
                  borderBottom: '1px solid lightgrey',
                  borderTop: '1px solid lightgrey',
                }}
              >
                <Form.Select
                  style={{
                    border: 'none',
                    width: '100%',
                    fontSize: 'small',
                    fontFamily: "'Times New Roman', Times, serif",
                  }}
                >
                  <option>{option}</option>
                  <option>ABC DEF GHI JKL MNO PQR S</option>
                </Form.Select>
              </div>
            ))}

        </Col>
        <Col md={1} className="d-flex justify-content-center align-items-center"></Col>
      </Row>

      
    </Container>


  );
};

export default Cloth;
