import React from "react";
import Slider from "react-slick";
import { Button } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const CardSlider = () => {
    const products = [
        {
            id: 1,
            title: "GREEN COTTON KURTA",
            price: "PKR 5,265.00",
            originalPrice: "PKR 10,530.00",
            discount: "50% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/3/2/32895_2_.jpg",
        },
        {
            id: 2,
            title: "MAROON COTTON KURTA",
            price: "PKR 4,712.00",
            originalPrice: "PKR 5,890.00",
            discount: "20% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33094_1_.jpg",
        },
        {
            id: 3,
            title: "BLACK COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/9/0/90441_3_.jpg",
        },
        {
            id: 4,
            title: "GREY COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33142_1_.jpg",
        },
    ];

    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 2,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <div className="my-8">
            <h1 className="text-center text-2xl font-bold mb-8">RELATED PRODUCTS</h1>
            <div className="px-6">
                <Slider {...sliderSettings}>
                    {products.map((product) => (
                        <div key={product.id} className="p-2">
                            <div className="flex flex-col items-center bg-white shadow-md rounded-lg overflow-hidden">
                                <img
                                    className="h-64 w-full object-cover"
                                    src={product.imageUrl}
                                    alt={product.title}
                                />
                                <div className="p-4 w-full">
                                    <p className="text-sm text-gray-700 truncate">
                                        {product.title}
                                    </p>
                                    <div className="flex justify-between items-center mt-2">
                                        <div>
                                            <h5 className="text-lg text-red-600 font-semibold">
                                                {product.price}
                                            </h5>
                                            {product.originalPrice && (
                                                <h5 className="text-sm text-gray-400 line-through">
                                                    {product.originalPrice}
                                                </h5>
                                            )}
                                        </div>
                                        {product.discount && (
                                            <span className="bg-red-500 text-white text-xs font-bold py-1 px-3 rounded-full">
                                                {product.discount}
                                            </span>
                                        )}
                                    </div>
                                    <div className="flex justify-center mt-4">
                                        <button className="bg-gray-900 text-white text-sm px-4 py-2 rounded-lg">
                                            Add to Bag
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
};

export default CardSlider;
