import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

const ProductDetails = () => {
  const [mainImage, setMainImage] = useState(
    "https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
  );

  const styles = {
    main: {
      height: "730px",
      width: "100%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    subbox: {
      height: "100%",
      width: "87%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    leftBox: {
      height: "100%",
      width: "48%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    rightBox: {
      height: "97%",
      width: "52%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    flexbox: {
      height: "720px",
      width: "450px",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
    },
    box1: {
      height: "78%",
      width: "100%",
      display: "flex",
      justifyContent: "space-evenly",
      alignItems: "center",
    },
    box2: {
      height: "18%",
      width: "70%",
      display: "flex",
      justifyContent: "space-evenly",
      alignItems: "center",
    },
    imgs: {
      height: "90%",
      width: "27%",
      cursor: "pointer",
    },
    productContainer: {
      height: "100%",
      maxWidth: "600px",
      margin: "50px auto",
      padding: "20px",
    },
    head: {
      fontSize: "22px",
    },
    sku: {
      fontSize: "14px",
      color: "#555",
      marginTop: "-10px",
    },
    reviewLink: {
      textDecoration: "underline",
      color: "#000",
      fontSize: "14px",
    },
    pricing: {
      margin: "15px 0",
      fontSize: "28px",
    },
    currentPrice: {
      fontWeight: "bold",
      marginRight: "10px",
    },
    oldPrice: {
      textDecoration: "line-through",
      color: "#777",
    },
    limitedStock: {
      fontSize: "17px",
      marginBottom: "15px",
    },
    addToBag: {
      display: "block",
      width: "60%",
      height: "50px",
      padding: "10px",
      border: "2px solid black",
      backgroundColor: "white",
      fontSize: "16px",
      textTransform: "uppercase",
      textAlign: "center",
    },
    addToBagHover: {
      backgroundColor: "#000",
      color: "white",
    },
    productDetails: {
      marginTop: "20px",
    },
    disclaimer: {
      fontSize: "15px",
      fontStyle: "italic",
    },
    bar: {
      width: "100%",
      display: "flex",
      flexDirection: "column",
    },
    barItems: {
      height: "40px",
      width: "100%",
      display: "flex",
      justifyContent: "left",
      borderBottom: "1px solid lightgrey",
      borderTop: "1px solid lightgrey",
    },
    barOptions: {
      border: "none",
      width: "100%",
      fontSize: "small",
      fontFamily: "'Times New Roman', Times, serif",
    },
  };

  return (
    <Container fluid style={styles.main}>
      <Row style={styles.subbox}>
        {/* Left Section */}
        <Col lg={6} md={12} style={styles.leftBox}>
          <div style={styles.flexbox}>
            <div style={styles.box1}>
              <img
                id="mainpic"
                src={mainImage}
                alt="Main Product"
                width="97%"
                height="99%"
              />
            </div>
            <div style={styles.box2}>
              <img
                style={styles.imgs}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                alt="Thumbnail 1"
                onClick={() =>
                  setMainImage(
                    "https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                  )
                }
              />
              <img
                style={styles.imgs}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_2_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                alt="Thumbnail 2"
                onClick={() =>
                  setMainImage(
                    "https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_2_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                  )
                }
              />
              <img
                style={styles.imgs}
                src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_3_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                alt="Thumbnail 3"
                onClick={() =>
                  setMainImage(
                    "https://www.junaidjamshed.com/media/catalog/product/2/4/24-1799_3_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
                  )
                }
              />
            </div>
          </div>
        </Col>

        {/* Right Section */}
        <Col lg={6} md={12} style={styles.rightBox}>
          <div style={styles.productContainer}>
            <p style={styles.head}>
              MULTICOLOR CAMBRIC PRINTED UNSTITCHED 3PC
            </p>
            <p style={styles.sku}>SKU#: 10000080093</p>
            <p style={styles.stockStatus}>IN STOCK</p>
            <p style={styles.reviewLink}>
              <a href="#">Be the first to review this product</a>
            </p>

            <div style={styles.pricing}>
              <span style={styles.currentPrice}>PKR 3,912.00</span>
              <span style={styles.oldPrice}>PKR 4,890.00</span>
            </div>
            <hr color="lightgrey" />
            <p style={styles.limitedStock}>
              ⚫ Limited Stock Alert: Get Yours Before They're Gone!
            </p>
            <Button style={styles.addToBag}>ADD TO BAG</Button>

            <div style={styles.productDetails}>
              <h2 style={{ textDecoration: "underline" }}>Basic 3 Piece</h2>
              <ul>
                <li>Digital Printed Cambric Shirt: 1.92 Meters</li>
                <li>Digital Printed Cambric Dupatta: 2.5 Meters</li>
                <li>Cambric Trouser: 2.5 Meters</li>
              </ul>
              <p style={styles.disclaimer}>
                * The stitch style and decorative accessories featured in the
                dress are for illustrative purposes only and are not included
                as part of the product.
              </p>
            </div>

            <div style={styles.bar}>
              <div style={styles.barItems}>
                <select style={styles.barOptions}>
                  <option>MORE INFORMATION</option>
                  <option>ABC DEF GHI JKL MNO PQR S</option>
                </select>
              </div>
              <div style={styles.barItems}>
                <select style={styles.barOptions}>
                  <option>REVIEWS</option>
                  <option>ABC DEF GHI JKL MNO PQR S</option>
                </select>
              </div>
            </div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default ProductDetails;
