import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Product = () => {
  return (
    <div className="flex flex-col items-center p-4 bg-white rounded-lg shadow-md max-w-xs md:max-w-sm mx-auto">
      {/* Product Image */}
      <img
        className="rounded-t-lg"
        src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1950_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
        alt="Product"
      />
      
      {/* Product Title */}
      <div className="mt-2 text-sm font-semibold text-gray-800 text-center">
        PINK KHAADI EMBROIDERED 3PC STITCHED
      </div>

      {/* Price and Discount */}
      <div className="flex justify-between items-center w-full mt-2">
        <div className="text-crimson font-bold text-lg">
          PKR 3,592.00
        </div>
        <div className="bg-crimson text-white px-2 py-1 text-sm rounded">
          20% OFF
        </div>
      </div>

      {/* Original Price */}
      <div className="text-gray-500 line-through text-lg mt-1">
        PKR 4,490.00
      </div>

      {/* Size Buttons */}
      <div className="flex justify-center gap-2 mt-4">
        {["3 M", "3-6", "6-9", "9-12", "12-"].map((size) => (
          <button
            key={size}
            className="border border-gray-300 rounded-full px-3 py-1 text-xs text-gray-500 hover:bg-gray-100"
          >
            {size}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Product;
