import React from "react";
import Slider from "react-slick";
import { Button, Row, Col } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const CardSlider = () => {
    const products = [
        {
            id: 1,
            title: "GREEN COTTON KURTA",
            price: "PKR 5,265.00",
            originalPrice: "PKR 10,530.00",
            discount: "50% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/3/2/32895_2_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds",
        },
        {
            id: 2,
            title: "MAROON COTTON KURTA",
            price: "PKR 4,712.00",
            originalPrice: "PKR 5,890.00",
            discount: "20% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33094_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds",
        },
        {
            id: 3,
            title: "BLACK COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/9/0/90441_3_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds",
        },
        {
            id: 4,
            title: "GREY COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33142_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds",
        },
    ];

    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                },
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 1,
                },
            },
        ],
    };

    return (
        <div className="my-4">
    <h1 style={{ fontSize: '30px', textAlign: 'center' }} className="text">
        RELATED PRODUCTS
    </h1>
    <br /><br /><br />
    <div style={{ padding: '0 70px' }}>
    <Slider {...sliderSettings}>
    {products.map((product, index) => (
        <div
            key={index}
            className="px-2 flex justify-center sm:px-3"
        >
            <div className="h-100" style={{ height: '280px', width: '250px' }}>
                <img
                    style={{ height: '250px', objectFit: 'cover' }}
                    src={product.imageUrl}
                    alt={product.title}
                />
                <div className="flex flex-col">
                    <p
                        className="text-truncate pt-1 text-sm"
                        style={{ fontSize: '13px' }}
                    >
                        {product.title}
                    </p>
                    <div className="flex justify-between items-center">
                        <div>
                            <b>
                                <h5
                                    className="text-danger"
                                    style={{ fontSize: '17px' }}
                                >
                                    {product.price}
                                </h5>
                            </b>
                            {product.originalPrice && (
                                <h5
                                    className="text-decoration-line-through m-0 p-0"
                                    style={{ fontSize: '17px' }}
                                >
                                    {product.originalPrice}
                                </h5>
                            )}
                        </div>
                        {product.discount && (
                            <span
                                className="bg-red-500 text-white text-sm flex items-center justify-center w-20 h-8"
                            >
                                {product.discount}
                            </span>
                        )}
                    </div>
                    <div className="mt-auto text-center">
                        <Button
                            variant="primary"
                            className="mt-3 w-1/2 bg-dark border-dark text-white"
                        >
                            Add to Bag
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    ))}
</Slider>


    </div>
</div>

    );
};

export default CardSlider;
