import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Product = () => {
  return (
    <div className="card text-center mx-auto" style={{ width: "13rem" }}>
      {/* Product Image */}
      <img
        className="card-img-top"
        src="https://www.junaidjamshed.com/media/catalog/product/2/4/24-1950_1_.jpg?width=436&height=560&canvas=436,560&optimize=medium&bg-color=255,255,255&fit=bounds"
        alt="Product"
        style={{ height: "270px" }}
      />

      {/* Card Body */}
      <div className="card-body">
        {/* Product Title */}
        <h5 className="card-title text-muted" style={{ fontSize: "small" }}>
          PINK KHAADI EMBROIDERED 3PC STITCHED
        </h5>

        {/* Price and Discount */}
        <div className="d-flex justify-content-between align-items-center">
          <span
            className="text-danger fw-bold"
            style={{ fontSize: "18px", display: "flex" }}
          >
            PKR 3,592.00
          </span>
          <span
            className="badge bg-danger text-white"
            style={{
              fontSize: "12x",
              height: "25px",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            20% OFF
          </span>
        </div>

        {/* Original Price */}
        <div
          className="text-muted text-decoration-line-through"
          style={{ fontSize: "large", display: "flex" }}
        >
          <b>PKR 4,490.00</b>
        </div>

        {/* Size Buttons */}
        <div className="d-flex justify-content-center gap-2 mt-3">
          {["3 M", "3-6", "6-9", "9-12", "12-"].map((size) => (
            <button
              key={size}
              className="btn btn-outline-secondary rounded-circle"
              style={{
                width: "25px",
                height: "25px",
                fontSize: "xx-small",
                padding: "2px",
              }}
            >
              {size}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Product;
