import React from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';
import ThreeBlock from './ThreeBlock';

function Sidebar() {
  return (
    <Container fluid>
      <Row>
        {/* Sidebar */}
        <Col xs={12} md={3} lg={2} style={{ position: 'sticky', top: '115px', height: '450px' }}>
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              height: '100%',
              width: '100%',
            }}
          >
            <div
              style={{
                height: '12%',
                display: 'flex',
                justifyContent: 'left',
                alignItems: 'center',
                fontSize: 'small',
                fontFamily: "'Times New Roman', Times, serif",
                color: 'darkslategrey',
                fontWeight: 'bold',
                borderBottom: '1px solid lightgrey',
                padding: '10px 0',
              }}
            >
              SHOPPING OPTIONS
            </div>

            {['COLOR FAMILY', 'SEASON', 'PRODUCT CATEGORY', 'SIZE', 'FABRIC'].map((option, index) => (
              <div
                key={index}
                style={{
                  height: '40px',
                  width: '100%',
                  display: 'flex',
                  justifyContent: 'left',
                  alignItems: 'center',
                  borderBottom: '1px solid lightgrey',
                }}
              >
                <Form.Select
                  style={{
                    border: 'none',
                    width: '95%',
                    fontSize: 'small',
                    fontFamily: "'Times New Roman', Times, serif",
                    color: 'darkslategrey',
                  }}
                >
                  <option>{option}</option>
                  <option>ABC DEF GHI JKL MNO PQR S</option>
                </Form.Select>
              </div>
            ))}
          </div>
        </Col>

        {/* Main Content */}
        <Col xs={12} md={9} lg={10}>
          <div style={{ padding: '20px' }}>
            <ThreeBlock />
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default Sidebar;
