import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

const ProductGrid = () => {
  const products = [
    {
      id: 1,
      title: "Blue Raw Silk Unstitched 3PC",
      price: "PKR 5,265.00",
      originalPrice: "PKR 10,530.00",
      discount: "50% OFF",
      imageUrl: "path_to_image_1", // Replace with actual image paths
    },
    {
      id: 2,
      title: "Multicolor Linen Printed Unstitched 3PC",
      price: "PKR 4,712.00",
      originalPrice: "PKR 5,890.00",
      discount: "20% OFF",
      imageUrl: "path_to_image_2",
    },
    {
      id: 3,
      title: "Brown Lawn Unstitched 3PC",
      price: "PKR 3,380.00",
      imageUrl: "path_to_image_3",
    },
    {
      id: 4,
      title: "Multicolor Lawn Unstitched 3PC",
      price: "PKR 3,380.00",
      imageUrl: "path_to_image_4",
    },
  ];

  return (
    <Container fluid className="my-4">
      <Row className="g-4">
        {products.map((product) => (
          <Col key={product.id} xs={12} sm={6} md={4} lg={3}>
            <Card className="h-100">
              <Card.Img variant="top" src={product.imageUrl} alt={product.title} />
              <Card.Body>
                <Card.Title className="text-truncate">{product.title}</Card.Title>
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <h6 className="text-danger">{product.price}</h6>
                    {product.originalPrice && (
                      <p className="text-muted text-decoration-line-through mb-0">
                        {product.originalPrice}
                      </p>
                    )}
                  </div>
                  {product.discount && (
                    <span className="badge bg-danger">{product.discount}</span>
                  )}
                </div>
                <Button variant="primary" className="mt-3 w-100">
                  Add to Bag
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default ProductGrid;
