import React from "react";
import Slider from "react-slick";
import { Button } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const CardSlider = () => {
    const products = [
        {
            id: 1,
            title: "GREEN COTTON KURTA",
            price: "PKR 5,265.00",
            originalPrice: "PKR 10,530.00",
            discount: "50% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/3/2/32895_2_.jpg",
        },
        {
            id: 2,
            title: "MAROON COTTON KURTA",
            price: "PKR 4,712.00",
            originalPrice: "PKR 5,890.00",
            discount: "20% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33094_1_.jpg",
        },
        {
            id: 3,
            title: "BLACK COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/9/0/90441_3_.jpg",
        },
        {
            id: 4,
            title: "GREY COTTON KURTA",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/j/jjk-33142_1_.jpg",
        },
    ];

    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 2,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <div className="my-4">
            <h1 style={{ fontSize: "30px", textAlign: "center" }}>RELATED PRODUCTS</h1>
            <div style={{ padding: "0 70px" }}>
                <Slider {...sliderSettings}>
                    {products.map((product) => (
                        <div key={product.id} className="px-2">
                            <div className="h-100" style={{ height: "280px", width: "250px" }}>
                                <img
                                    style={{ height: "250px", objectFit: "cover" }}
                                    src={product.imageUrl}
                                    alt={product.title}
                                />
                                <div className="d-flex flex-column">
                                    <p
                                        style={{
                                            paddingTop: "5px",
                                            fontSize: "13px",
                                        }}
                                        className="text-truncate"
                                    >
                                        {product.title}
                                    </p>
                                    <div className="d-flex justify-content-between align-items-center">
                                        <div>
                                            <b>
                                                <h5
                                                    style={{ fontSize: "17px" }}
                                                    className="text-danger"
                                                >
                                                    {product.price}
                                                </h5>
                                            </b>
                                            {product.originalPrice && (
                                                <h5
                                                    style={{
                                                        fontSize: "17px",
                                                    }}
                                                    className="text-decoration-line-through mb-0"
                                                >
                                                    {product.originalPrice}
                                                </h5>
                                            )}
                                        </div>
                                        {product.discount && (
                                            <span
                                                className="badge bg-danger"
                                                style={{
                                                    width: "80px",
                                                    height: "30px",
                                                    fontSize: "13px",
                                                    display: "inline-flex",
                                                    alignItems: "center",
                                                    justifyContent: "center",
                                                }}
                                            >
                                                {product.discount}
                                            </span>
                                        )}
                                    </div>
                                    <div className="text-center mt-auto">
                                        <Button
                                            variant="primary"
                                            className="mt-3 w-50 bg-dark border-dark text-white"
                                        >
                                            Add to Bag
                                        </Button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
};

export default CardSlider;
