import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const CardSlider = () => {
    const products = [
        {
            id: 1,
            title: "BLUE RAW SILK UNSTITCHED 3PC",
            price: "PKR 5,265.00",
            originalPrice: "PKR 10,530.00",
            discount: "50% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/4/24-035_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
        },
        {
            id: 2,
            title: "MULTICOLOR LINEN PRINTED UNSTITCHED 3PC",
            price: "PKR 4,712.00",
            originalPrice: "PKR 5,890.00",
            discount: "20% OFF",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/3/23-430_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
        },
        {
            id: 3,
            title: "BROWN LAWN UNSTITCHED 3PC",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/2/3/23-602_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
        },
        {
            id: 4,
            title: "MULTICOLOR LAWN UNSTITCHED 3PC",
            price: "PKR 3,380.00",
            imageUrl: "https://www.junaidjamshed.com/media/catalog/product/j/s/jst-24-1649_1_.jpg?optimize=medium&bg-color=255,255,255&fit=bounds&height=640&width=640&canvas=640:640&dpr=2",
        },
    ];

    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                },
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 2,
                },
            },
        ],
    };

    return (
        <div className="my-10">
            <h1 className="text-center text-2xl font-bold">RELATED PRODUCTS</h1>
            <div className="mt-8 px-8">
                <Slider {...sliderSettings}>
                    {products.map((product) => (
                        <div key={product.id} className="px-2">
                            <div className="h-full w-64">
                                <img
                                    className="h-64 w-full object-cover"
                                    src={product.imageUrl}
                                    alt={product.title}
                                />
                                <div className="flex flex-col mt-2">
                                    <p className="text-sm truncate">{product.title}</p>
                                    <div className="flex justify-between items-center mt-2">
                                        <div>
                                            <h5 className="text-red-600 font-bold text-lg">{product.price}</h5>
                                            {product.originalPrice && (
                                                <h5 className="text-gray-500 line-through text-sm">
                                                    {product.originalPrice}
                                                </h5>
                                            )}
                                        </div>
                                        {product.discount && (
                                            <span className="bg-red-600 text-white text-xs font-semibold py-1 px-2 rounded">
                                                {product.discount}
                                            </span>
                                        )}
                                    </div>
                                    <button className="mt-3 py-2 bg-black text-white text-sm font-semibold rounded w-full">
                                        Add to Bag
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
};

export default CardSlider;
